from PiStorms_GRX import RCServo
import time
import random

servo_BAS1 = RCServo("BAS1")

servo_BAS2 = RCServo("BAS2")

servo_BBS1 = RCServo("BBS1")


# continuous rotation servos
servo_BAS1.setSpeed(50)
servo_BAS2.setSpeed(-50)
time.sleep(3)
servo_BAS1.stop()
servo_BAS2.stop()
time.sleep(1)

# "normal" servos
servo_BBS1.setPos(0)
time.sleep(2)
servo_BBS1.setPos(90)
time.sleep(2)
servo_BBS1.setPos(180)
time.sleep(1)

# move servo to 10 random positions
for count in range(10):
  servo_BBS1.setPos((random.randint(0, 180)))
  time.sleep(0.5)
