from PiStorms import PiStorms
import time
import GroveDevices

psm = PiStorms()

groveButton_BAD1 = GroveDevices.Grove_Button("BAD1")

groveLuminanceSensor_BAA1 = GroveDevices.Grove_Luminance_Sensor("BAA1")

groveLoudnessSensor_BAA2 = GroveDevices.Grove_Loudness_Sensor("BAA2")

groveUVSensor_BAA3 = GroveDevices.Grove_UV_Sensor("BAA3")


psm.screen.drawDisplay('Sensor Demo', True)
psm.screen.termPrintln('BAD1: Grove Touch Sensor')
psm.screen.termPrintln('BAA1: Grove Luminance Sensor')
psm.screen.termPrintln('BAA2: Grove Loduness Sensor')
psm.screen.termPrintln('BAA3: Grove UV Sensor')
time.sleep(4)
while not (psm.screen.isTouched()):
  if groveButton_BAD1.isPressed():
    psm.led(1, 255, 0, 0)
  else:
    psm.led(1, 51, 255, 51)
  psm.screen.termPrintAt(0, groveButton_BAD1.isPressed())
  psm.screen.termPrintAt(1, groveLuminanceSensor_BAA1.luminance())
  psm.screen.termPrintAt(2, groveLoudnessSensor_BAA2.detectSound())
  psm.screen.termPrintAt(3, groveUVSensor_BAA3.UVindex())
